import '../App.css';
import { useState } from 'react';
import resume from "../assets/resume.pdf"
import profilePic from "../images/profile.jpeg";
import Social from "./Social.js";

function Profile() {
    const openResume = () => {
        window.open(resume);
    }
    const openContact = () => {
        window.location.href = './#contact';
    }
    return (
        <section id="profile">
            <div className="section__pic-container">
                <img id="profile_pic" src={profilePic} alt="tushar's profile" />
            </div>
            <div className="section__text">
                <p className='section_text_p1'>Hello, I'm</p>
                <h1>Tushar Mahat</h1>
                <p className='section__text_p2'>Full Stack Developer</p>
                <div className='btn-container'>
                    <button className="btn btn-color-2" onClick={openResume}>Download resume</button>
                    <button className="btn btn-color-1" onClick={openContact}>Contact Info</button>
                </div>
                <Social />
            </div>
        </section>
    );
}

export default Profile;
