import '../App.css';
import { useState } from 'react';
import aboutPic from "../images/about.jpg"
import experiencePic from "../images/experience.png"
function About() {
    const openLinkedIn = () => {
        window.location.href = 'https://www.linkedin.com/in/tushar-mahat/';
    }
    const openGitHub = () => {
        window.location.href = 'https://github.com/tusharmahat';
    }
    return (
        <section id="about">
            <p className='section__text__p1'>Get To Know More..</p>
            <h1 className='title'>About Me</h1>
            <div className='section-container'>
                <div className='section__pic-container'>
                    <img src={aboutPic} className='about' alt="profile" />
                </div>
                <div className='about-details-container'>
                    <div className='about-containers'>
                        <div className='details-container'>
                            <img src={experiencePic} className='icon-about' alt="experience" />
                            <h1>Education</h1>
                            <p>B.Sc<br />Major in Computing Science With Minor in Mathematics</p>
                        </div>
                        <div className='details-container'>
                            <img src={experiencePic} className='icon-about' alt="experience" />
                            <h1>Academics</h1>
                            <p>Bachelor of Science<br />Major in Computing Science With Minor in Mathematics</p>
                        </div>
                    </div>
                    <div className='text-container'></div>
                </div>
            </div>
        </section>
    );
}

export default About;
