import '../App.css';
import { useState } from 'react';
import resume from "../assets/resume.pdf"
import profilePic from "../images/profile.jpeg";
function Profile() {
    const openResume = () => {
        window.open(resume);
    }

    return (
        <section id="profile">
            <div className="section__pic-container">
                <img src={profilePic} alt="tushar's profile picture" />
            </div>
            <div className="section__text">
                <p className='section_text_p1'>Hello, I'm</p>
                <h1>Tushar Mahat</h1>
                <p className='section__text_p2'>Full Stack Developer</p>
                <div className='btn-container'>
                    <button className="btn btn-color-2" onClick={openResume}>Download resume</button>
                </div>
            </div>

        </section>
    );
}

export default Profile;
